import React from 'react';
import './Item.css';
import { motion } from 'framer-motion';

const Item = ({ icon, name }) => {
    return (
        <motion.div
            className='item'
            whileHover
        >
            <motion.div className='icon'>
                {icon}
            </motion.div>
            <motion.span>
                {name}
            </motion.span>
        </motion.div>
    )
}

export default Item
