import React, { useState } from 'react';
import './App.css';
import { AccountCircleRounded, AssignmentTurnedInRounded, AttachMoneyRounded, BarChartRounded, ColorLensRounded, DashboardRounded, SettingsRemoteRounded, TocRounded } from '@material-ui/icons';
import Item from './components/Item';
import { motion } from 'framer-motion';

const App = () => {

  const [open, setOpen] = useState(true);

  // for collapsing sidebar
  const handleToggle = () => {
    setOpen(!open)
  }

  const sideContainerVariants = {
    true: {
      width: '15rem',
    },
    false: {
      transition: {
        delay: 0.6
      }
    }
  }

  const sidebarVariants = {
    true: {},
    false: {
      width: '3rem',
      transition: {
        delay: 0.4
      }
    }
  }

  const profileVariants = {
    true: {
      alignSelf: 'center',
      width: '4rem'
    },
    false: {
      alignSelf: 'flex-start',
      marginTop: '2rem',
      width: '3rem'
    }
  }

  return (
    <div className='App'>
      <motion.div
        data-Open={open}
        variants={sideContainerVariants}
        initial={`${open}`}
        animate={`${open}`}
        className='sidebar-container'
      >
        {/* sidebar div */}
        <motion.div className='sidebar'
          initial={`${open}`}
          animate={`${open}`}
          variants={sidebarVariants}
        >

          {/* Lines Icon */}
          <motion.div
            whileHover={{
              scale: 1.2,
              rotate: 180,
              backgroundColor: "rgba(255, 255, 255, 0.3)",
              backdropFilter: "blur(3.5px)",
              WebkitBackdropFilter: "blur(3.5px)",
              border: "1px solid rgba(255, 255, 255, 0.18)",
              transition: {
                delay: 0.2,
                duration: 0.4
              }
            }}
            onClick={handleToggle}
            className='lines-icon'>
            <TocRounded />
          </motion.div>

          {/* Profile Image */}
          <motion.div
            layout
            className='profile'
            initial={`${open}`}
            animate={`${open}`}
            variants={profileVariants}
            transition={{ duration: 0.4 }}
            whileHover={{
              backgroundColor: "rgba(255, 255, 255, 0.3)",
              boxShadow: "0 8px 32px 0 rgba(31, 38, 135, 0.37)",
              backdropFilter: "blur(5.5px)",
              WebkitBackdropFilter: "blur(3.5px)",
              border: "1px solid rgba(255, 255, 255, 0.18)",
              cursor: "pointer"
            }}
          >
            <img
              src="https://ae01.alicdn.com/kf/H5be6a0fa5f584a8a8420da2a7d4bc809r/RBRARE-Polaroid-Men-s-Goggle-Driving-Sunglasses-Men-Classic-Low-Profile-Sun-Glasses-For-Men-High.jpg"
              alt='profile'
            />
          </motion.div>

          {/* Content */}
          <div className='groups'>
            <div className='group'>
              <motion.div
                initial={`${open}`}
                animate={`${open}`}
                variants={h3Variants}
              >
                Analytics
              </motion.div>
              <Item
                icon={<DashboardRounded />}
                name='Dashboard'
              />
              <Item
                icon={<BarChartRounded />}
                name='Performance'
              />
            </div>
          </div>
          <div className='groups'>
            <div className='group'>
              <motion.div>Content</motion.div>
              <Item
                icon={<AttachMoneyRounded />}
                name='Sales'
              />
              <Item
                icon={<AssignmentTurnedInRounded />}
                name='Checkout'
              />
              <Item
                icon={<AccountCircleRounded />}
                name='Customers'
              />
            </div>
          </div>
          <div className='groups'>
            <div className='group'>
              <motion.div>Customization</motion.div>
              <Item
                icon={<SettingsRemoteRounded />}
                name='Segments'
              />
              <Item
                icon={<ColorLensRounded />}
                name='Themes'
              />
            </div>
          </div>
        </motion.div>
      </motion.div>
      <div className='body-container'>

      </div>
    </div >
  )
}

export default App
